fun esPar(numero: Int): Boolean {
    return when {
        numero % 2 == 0 -> true
        else -> false
    }
}

fun signo(numero: Int): String{
    return when{
        numero < 0 -> "Negativo"
        numero > 0 -> "Positivo"
        else -> "Cero"
    }
}

fun mostrarNumerosConLoops() {
    // Usando for
    println("Usando for:")
    for (i in 1..10) {
        println(i)
    }
    
    // Usando while
    println("\nUsando while:")
    var j = 1
    while (j <= 10) {
        println(j)
        j++
    }
    
    // Usando do-while
    println("\nUsando do-while:")
    var k = 1
    do {
        println(k)
        k++
    } while (k <= 10)
}

fun mostrarNumerosParesConLoops() {
    // Usando for
    println("Numeros pares del 2 al 20:")
    println("\nUsando for:")
    for (i in 2..20) {
        if (i % 2 === 0) println(i)
    }
    
    // Usando while
    println("\nUsando while:")
    var j = 2
    while (j <= 20) {
        if (j % 2 === 0) println(j)
        j++
    }
    
    // Usando do-while
    println("\nUsando do-while:")
    var k = 2
    do {
        if (k % 2 === 0) println(k)
        k++
    } while (k <= 20)
}

fun factorial(n: Int): Int {
    return if (n <= 1) 1 else n * factorial(n - 1)
}

fun saludar(receptor: String = "Quinteros", emisor: String = "Ivo") {
    println("Hola $receptor, soy $emisor, ya casi estamos llegando al final de mi tarea")
}

fun potencia(base: Int, exponente: Int):Int{
    var resultado: Int = 1
    for (i in 1..exponente){
        resultado = resultado * base
    }
    return resultado
}

fun demostrarAlcanceVariables() {
    // Variable local en el ámbito de la función
    val variableGlobal = "Soy global en esta función"
    
    // Bloque if con su propio ámbito
    if (true) {
        val variableIf = "Soy local del if"
        println("Dentro del if: $variableIf")
        println("Puedo acceder a: $variableGlobal")
    }
    
    // Bloque for con su propio ámbito
    for (i in 1..3) {
        val variableFor = "Soy local del for - iteración $i"
        println("Dentro del for: $variableFor")
        println("Puedo acceder a: $variableGlobal")
    }
    
    // Intentar acceder a variables locales fuera de su ámbito
    // println(variableIf)  // Esto daría error
    // println(variableFor) // Esto daría error
}

