fun encontrarMaxMin(numeros: List<Int>): List<Int> {
    var min = numeros[0]
    var max = numeros[0]
    for (i in numeros){
        if (i<min){
            min = i
        } else {
            if (i>max){
                max = i
            }
        }
    }
    return listOf(min, max)
}